pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
---
{{inject 'productId' product.id}}
{{inject "bearerToken" settings.storefront_api.token}}
{{inject "showSwatch" true}}
{{inject "currency_code" currency_selector.active_currency_code}}
{{#partial "page"}}

    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#each product.shipping_messages}}
        {{> components/common/alert/alert-info message}}
    {{/each}}

    <div>
        {{> components/products/product-view}}

        {{{region name="product_below_content"}}}
        {{{region name="region_above_footer--global"}}}

        {{#if product.videos.list.length}}
            {{> components/products/videos product.videos}}
        {{/if}}

        {{#all settings.show_product_reviews theme_settings.show_product_reviews (if theme_settings.show_product_details_tabs '!==' true)}}
            {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
        {{/all}}

        {{> components/products/tabs}}
    </div>

    {{> components/products/schema}}
{{/partial}}
{{> layout/base}}

components/products/reviews:
{{#if reviews.total}}
<section class="toggle productReviews" id="product-reviews" data-product-reviews>
    <h4 class="toggle-title">
    {{lang 'products.reviews.header' total=reviews.total}}
    {{#if theme_settings.show_product_reviews}}
    <a class="toggleLink is-open" data-collapsible href="#productReviews-content">
        <span class="toggleLink-text toggleLink-text--on">
            {{lang 'products.reviews.hide'}}
        </span>
        <span class="toggleLink-text toggleLink-text--off">
            {{lang 'products.reviews.show'}}
        </span>
    </a>
    {{/if}}
    </h4>
    <div class="toggle-content is-open" id="productReviews-content" aria-hidden="false">
        <div class="swiper reviewSwiper">
            <div class="swiper-wrapper" id="productReviews-list">
                {{#each reviews.list}}
                <div class="productReview swiper-slide">
                    <article>
                        <header>
                            <span class="productReview-rating rating--small">
                                {{> components/products/ratings rating=rating}}
                                <span class="productReview-ratingNumber">{{ rating }}</span>
                            </span>
                            <h5 class="productReview-title">{{{ sanitize title }}}</h5>
                            {{#if name}}
                            <p class="productReview-author">
                                {{{lang 'products.reviews.post_on_by' date=date name=(sanitize name) }}}
                            </p>
                            {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                            {{/if}}
                        </header>
                        <p class="productReview-body">{{{ sanitize text }}}</p>
                    </article>
                </div>
                {{/each}}
            </div>
            <div class="swiper-pagination"></div>
        </div>
        {{> components/common/paginator pagination.reviews reload=true}}
    </div>
</section>
{{/if}}

